$(document).ready(function () {
  $(window)
    .scroll(function () {
      $(this).scrollTop() < 1e3
        ? $("#button-scrolltop").removeClass("scrolltop")
        : $("#button-scrolltop").addClass("scrolltop");
    })
    .trigger("scroll"),
    $(".slider").bxSlider({
      auto: !0,
      mode: "fade",
      speed: 800,
      pause: 1e4,
      autoDelay: 1e3,
      nextText: '<i class="fas fa-chevron-right"></i>',
      prevText: '<i class="fas fa-chevron-left"></i>',
    }),
    $("#scrolltop").click(function () {
      $("html, body").animate({ scrollTop: 0 }, 1500),
        setTimeout(function () {
          $("#button-scrolltop").addClass("scrolltop");
        }, 800),
        setTimeout(function () {
          $("#scrolltop").addClass("displaynone");
        }, 1e3);
    });
  const o = Array.from($(".bx-controls .bx-pager-item"));
  Array.from($(".slider__item-img")).map((e) => {
    let s = $(e).attr("data-id");
    $(o[s]).addClass("pageSlide hoverSlide hoverSlide" + s);
  }),
    $(".item-child-after").click(function (o) {
      $(this).parent().children(".sub-menu").toggleClass("sub-menu-open"),
        o.preventDefault();
    }),
    $("main").click(function () {
      $(".header__menu-dropdown").hasClass("open-dropdown") &&
        $(".header__menu-dropdown").removeClass("open-dropdown");
    }),
    $(".nav-trigger").click(function () {
      $(".header__menu-dropdown").toggleClass("open-dropdown");
    });
});
